    private String SOAP_HEADER = "<soapenv:Envelope xmlns:soapenv='http://schemas.xmlsoap.org/soap/envelope/' xmlns:urn='urn:fiber.kpn.com:crm:frontdesk:data:v0'><soapenv:Header></soapenv:Header><soapenv:Body>";
    private String SOAP_FOOTER = "</soapenv:Body></soapenv:Envelope>";

    public String wrapSOAPEnvelope(String response) {
        return SOAP_HEADER + response + SOAP_FOOTER;
    }

    private String fetchResponse(String fileName, String ... params) {
        String response = null;
        String searchStr = "";
        for (String x : params) {
            searchStr += x + "|";
        }

        try (BufferedReader br = new BufferedReader(new FileReader(fileName))) {

            String line;
            while ((line = br.readLine()) != null) {
                if (line.startsWith(searchStr)) {
                    response = line.substring(line.lastIndexOf(searchStr));
                    break;
                }
            }

        } catch (IOException e) {
            e.printStackTrace();
        }

        return response;
    }

    public String getCustomerDetailsResponse() {
        String customerNumber="9013063677";
        String response = fetchResponse("C:/sourceCodes/mockServices/src/main/resources/customerDetails.txt", new String[]{customerNumber});

        return wrapSOAPEnvelope(response);
    }

